import React,{useState,useEffect} from 'react';
import {View,Text,TouchableOpacity,Button,SafeAreaView,KeyboardAvoidingView,Picker,TextInput,Platform,ScrollView} from 'react-native';
import {css} from '../assets/Css/css';
import {Table,Row,Rows,TableWrapper} from 'react-native-table-component';
import { LineChart } from 'react-native-chart-kit';

const MyLineChart = () => {
    return (
      <>
        <Text style={styles.header}>Line Chart</Text>
        <LineChart
          data={{
            labels: ['January', 'February', 'March', 'April', 'May', 'June'],
            datasets: [
              {
                data: [20, 45, 28, 80, 99, 43],
                strokeWidth: 2,
              },
            ],
          }}
          width={Dimensions.get('window').width - 16}
          height={220}
          chartConfig={{
            backgroundColor: '#1cc910',
            backgroundGradientFrom: '#eff3ff',
            backgroundGradientTo: '#efefef',
            decimalPlaces: 2,
            color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
            style: {
              borderRadius: 16,
            },
          }}
          style={{
            marginVertical: 8,
            borderRadius: 16,
          }}
        />
      </>
    );
  };


export default function Preditor2 ({route}) {
    const[result,setResult] = useState(route.params?.transf.data[0].val_fuzz)
    const date = [];
    const close = [];
    for(var i = 0; i <route.params?.transf.data[0].tam; i++){
        date.push(route.params?.transf.data[i].data);
    }
    for(var i = 0; i <route.params?.transf.data[0].tam; i++){
        close.push(route.params?.transf.data[i].close);
    }
    const Valores = {
        tableHead : ['Ativo'].concat(date,['Recomendação']),
        tableBody : [route.params?.transf.data[0].ativo].concat(close,[route.params?.transf.data[0].val_fuzz])
    }
    console.log(date);
    console.log(close);
    return (
        <SafeAreaView style = {{flex:1}}>
            <View> <Text>Teste</Text></View>
            <View  style = {{flex:1, padding:10}}>
              <Table borderStyle = {{borderWidth: 1}}>
                        <Row data = {Valores.tableHead} style = {{backgroundColor:'lightgray'}}/>
                        <Row data = {Valores.tableBody} style = {{backgroundColor:'lightgray'}}/>
              </Table>
            </View>
<View>
  <Text>Bezier Line Chart</Text>
  <View>
        <MyLineChart />
    </View>
</View>
        </SafeAreaView>
    );
    
}
