import React,{useState,useEffect} from 'react';
import {View,Text,TouchableOpacity,Button,StyleSheet,SafeAreaView,KeyboardAvoidingView,Dimensions,Picker,TextInput,Platform,ScrollView} from 'react-native';
import {css} from '../assets/Css/css';
import {Table,Row,Rows,TableWrapper} from 'react-native-table-component';
import {LineChart} from 'react-native-chart-kit';

function Grafico ({route}) {
    const teste = ['January', 'February', 'March', 'April', 'May', 'June'];
    const date = [];
    const close = [];
    for(var i = 0; i <route.params?.transf.data[0].tam; i++){
        date.push(route.params?.transf.data[i].data);
    }
    for(var i = 0; i <route.params?.transf.data[0].tam; i++){
        close.push(route.params?.transf.data[i].close);
    }
  return (
    <SafeAreaView style={{flex: 1}}>
        <Text>{date} {close}</Text>
      <ScrollView>
        <View style={styles.container}>
          <View>
          <Text style={styles.header}>Line Chart</Text>
      <LineChart
        data={{
          labels: date,
          datasets: [
            {
              data: close,
              strokeWidth: 1,
            },
          ],
        }}
        width={Dimensions.get('window').width - 16}
        height={220}
        chartConfig={{
          backgroundColor: '#1cc910',
          backgroundGradientFrom: '#eff3ff',
          backgroundGradientTo: '#efefef',
          decimalPlaces: 2,
          color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
          style: {
            borderRadius: 16,
          },
        }}
        style={{
          marginVertical: 8,
          borderRadius: 16,
        }}
      />
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

