import React,{useState} from 'react';
import {View,Text,TouchableOpacity,Button,SafeAreaView,KeyboardAvoidingView,Picker,TextInput,Platform} from 'react-native';
import {css} from '../assets/Css/css';
import { TextInputMask } from 'react-native-masked-text';

export default function Teste1 (props1) {
    const[display,setDisplay] = useState('none')
    const[bolsa,setBolsa] = useState(null)
    const[acao,setAcao] = useState(null)
    const[date,setDate] = useState(null)
    const[errorDate,setErrorDate] = useState(null)
    const validar = () => {if ({date} == null || length({date})!= 10 ){  setErrorDate("Digite corretamente");} };
    return (
        <SafeAreaView style = {css.fundo}>
          <KeyboardAvoidingView behavior = {Platform.OS == 'ios'?'padding':'height'} style = {css.fundo}>
            <View>
              <Text > {acao} {date} {bolsa} </Text>
            </View>
        
            <View style = {css.login_form}>
                <Picker  style={{ height: 50, width: 180,marginRight: 150, }} selectedValue = {bolsa} onValueChange ={(item,itemIndex) => {setBolsa(item)}}>
                    <Picker.Item label="Bovespa" value="^BVSP" />
                    <Picker.Item label="Dow Jones Industrial Average" value="^DJI" />	
                    <Picker.Item label="NASDAQ Composto" value="^IXIC" />
                </Picker>
                <Picker  style={{ height: 50, width: 400,marginRight: 50 }} selectedValue = {acao} onValueChange ={(item,itemIndex) =>{setAcao(item)}}>
                    <Picker.Item label="Banco do Brasil" value="BBAS3.SA" />
                    <Picker.Item label="Petrobras" value="PETR4.SA" />	
                    <Picker.Item label="Vale" value="VALE5.SA" />
                </Picker>
                <TextInputMask type={'datetime'} options={{format: 'DD/MM/YYYY'}} errorMessage = {errorDate} placeholder = 'DD/MM/YYYY' value={date} onChangeText={text => {setDate(text)}}/>
            </View>
            <View>
                <TouchableOpacity title = "Predição" onPress={()=>props1.navigation.navigate('Preditor2')} style = {css.login_button}><Text style = {css.login_buttonText}>Preditor2</Text> </TouchableOpacity>
            </View>
            </KeyboardAvoidingView>
        </SafeAreaView>
    );
    
}
