import React,{useState,useEffect} from 'react';
import {View,Text,TouchableOpacity,Button,SafeAreaView,KeyboardAvoidingView,Picker,TextInput,Platform,ScrollView} from 'react-native';
import {css} from '../assets/Css/css';
import { TextInputMask } from 'react-native-masked-text';
import config from "../config/config.json";

export default function Preditor1 (props ) {
    const[result,setResult] = useState('none')
    const[bolsas,setBolsas] = useState("^BVSP" )
    const[acao,setAcao] = useState('ABCB4.SA')
    const[date,setDate] = useState(null)
    const[transf,setTransf] = useState(null)
    const [vetAcoes,setVetAcoes] = useState(null);
    useEffect(()=>{bolsa();},[bolsas]);
    //useEffect(()=>{resultado1();},[acao])
    const teste = ()=>props.navigation.navigate('Preditor2', {transf});
    const fim = ()=>{resultado();teste()};
    async function bolsa(){
        let reqs = await fetch(config.urlRootPhython +'bolsa/', {method:'POST', headers: {'Accept':'application/json','Content-Type':'application/json'},body: JSON.stringify({bag: bolsas})});
        let resp = await reqs.json();
        setVetAcoes(resp.data);
        console.log(resp);
    }
    async function resultado1(){
        let reqs = await fetch(config.urlRootPhython +'resultado1/', {method:'POST', headers: {'Accept':'application/json','Content-Type':'application/json'},body: JSON.stringify({stock: acao, bag: bolsas})});
        let resp = await reqs.json();
        setResult(resp.data);
        setTransf(resp);
        console.log(resp);
    }
    return (
        <SafeAreaView style = {css.fundo}>
          <KeyboardAvoidingView behavior = {Platform.OS == 'ios'?'padding':'height'} style = {css.fundo}>
            <Text>{acao}</Text>
            <View style = {css.login_form}>
                <Picker  style={{ height: 50, width: 180,marginRight: 150, }} selectedValue = {bolsas} onValueChange ={(item,itemIndex) => {setBolsas(item)}}>   
                    <Picker.Item label="B3" value="^BVSP" />
                    <Picker.Item label="Bolsa de Nova York" value="^NYA" />
                    <Picker.Item label="NASDAQ" value="^IXIC" />
                    <Picker.Item label="Bolsa de Londres" value="^FTSE" />
                    <Picker.Item label="Bolsa da Alemanaha" value="^GDAXI" />
                    <Picker.Item label="Bolsa da Franca" value="^FCHI" />
                    <Picker.Item label="Bolsa de TÃ³quio" value="^N225" />
                    <Picker.Item label="Bolsa de Hong Kong" value="^HSI" />
                    <Picker.Item label="Bolsa da Australia" value="^AXJO" />
                    <Picker.Item label="Bolsa da Argentina" value="^MERV" />
                    <Picker.Item label="Bolsa do MÃ©xico" value="^MXX" />

                </Picker>
                <Picker  style={{ height: 50, width: 400,marginRight: 50 }} selectedValue = {acao} onValueChange ={(item,itemIndex) =>{setAcao(item)}}>
                    { vetAcoes && (vetAcoes.map((item,index)=><Picker.Item label = {item.full_name} value = {item.symbol}/>))}
                </Picker>
            </View>
            {/*<ScrollView>
                {result && (result.map((elem,id) => {return(<View key = {id}><Text>{elem.data}</Text></View>)}))}

    </ScrollView>*/}
            <View>
                <TouchableOpacity title = "Preditor1" onPress={()=>fim()} style = {css.login_button}><Text style = {css.login_buttonText}>Preditor2</Text> </TouchableOpacity>
            </View>
            </KeyboardAvoidingView>
        </SafeAreaView>
    );   
}
