import React,{useState,useEffect} from 'react';
import {View,Text,TouchableOpacity,Button,SafeAreaView,KeyboardAvoidingView,Picker,TextInput,Platform} from 'react-native';
import {css} from '../assets/Css/css';
import { TextInputMask } from 'react-native-masked-text';
import config from "../config/config.json";

export default function Preditor1 (props1 ) {
    const[display,setDisplay] = useState('none')
    const[country,setCountry] = useState('brazil')
    const[stocks,setStocks] = useState(null)
    const[date,setDate] = useState(null)
    const[errorDate,setErrorDate] = useState('oi')
    const [vetAcoes,setvetAcoes] = useState(null);
    const next =()=>{props1.navigation.navigate('Preditor2')};
    const etapa1 = (item) => {setCountry(item),bolsa()}

    async function bolsa(){
        let reqs = await fetch(config.urlRootPhython +'bolsa/', {method:'POST', headers: {'Accept':'application/json','Content-Type':'application/json'},body: JSON.stringify({pais: country})});
        let resp = await reqs.json();
        //resp.data.map((elem)=>{})
        console.log(resp);
    }
    return (
        <SafeAreaView style = {css.fundo}>
          <KeyboardAvoidingView behavior = {Platform.OS == 'ios'?'padding':'height'} style = {css.fundo}>
            <View>
              <Text >{stocks}</Text>
            </View>
        
            <View style = {css.login_form}>
                <Picker  style={{ height: 50, width: 180,marginRight: 150, }} selectedValue = {country} onValueChange ={(item,itemIndex) => {setCountry(item)}}>
                    <Picker.Item label="Brasil" value="brazil" />
                    <Picker.Item label="Estados Unidos" value="usa" />
                    <Picker.Item label="China" value="^IXIC" />
                </Picker>
                <Picker  style={{ height: 50, width: 400,marginRight: 50 }} selectedValue = {stocks} onValueChange ={(item,itemIndex) =>{etapa1(item)}}>
                    <Picker.Item label="Banco do Brasil" value="BBAS3.SA"/>
                    <Picker.Item label="Petrobras" value="PETR4.SA"  />	
                    <Picker.Item label="Vale" value="VALE5.SA"/>
                </Picker>
            </View>
            <View>
                <TouchableOpacity title = "Preditor1" onPress={total} style = {css.login_button}><Text style = {css.login_buttonText}>Preditor2</Text> </TouchableOpacity>
            </View>
            </KeyboardAvoidingView>
        </SafeAreaView>
    );
    
}
