import React,{useState,useEffect} from 'react';
import {View,Text,TouchableOpacity,Button,SafeAreaView,KeyboardAvoidingView,Picker,TextInput,Platform} from 'react-native';
import {css} from '../assets/Css/css';
import { TextInputMask } from 'react-native-masked-text';
import config from "../config/config.json";

export default function Preditor1 (props1 ) {
    const[display,setDisplay] = useState('none')
    const[bolsas,setBolsa] = useState('teste')
    const[acao,setAcao] = useState('teste')
    const[date,setDate] = useState(null)
    const[errorDate,setErrorDate] = useState('oi')
    const [vetAcoes,setvetAcoes] = useState(null);
    //const sergio = () => {bolsas === null;()=>{};acoes()}
    //useEffect(()=>{acoes();},[]);
    //Metodo para receber dados do python
    async function acoes(){
        let reqs = await fetch(config.urlRootPhython +'acoes/', {method:'GET', headers: {'Accept':'application/json','Content-Type':'application/json'}})
        let resp = await reqs.json();
        console.log(resp);
        
    }
    //useEffect(()=>{bolsa();},[]);
    //Metodos para enviar dados para o backend
    async function bolsa(){
        let reqs1 = await fetch(config.urlRootPhython +'bolsa/', {method:'POST', headers: {'Accept':'application/json','Content-Type':'application/json'},body: JSON.stringify({bag: bolsas})});
        let resp1 = await reqs1.json();
        console(resq1);
    }
    const teste2 = ()=>props1.navigation.navigate('TestePreditor2');
    const total =() => {bolsa();acoes();teste2()} 
    //const validar = () => {if ({date} == null ){  setErrorDate("Digite corretamente");} };
    return (
        <SafeAreaView style = {css.fundo}>
          <KeyboardAvoidingView behavior = {Platform.OS == 'ios'?'padding':'height'} style = {css.fundo}>
            <View>
              <Text >{acao}</Text>
            </View>
        
            <View style = {css.login_form}>
                <Picker  style={{ height: 50, width: 180,marginRight: 150, }} selectedValue = {bolsas} onValueChange ={(item,itemIndex) => {setBolsa(item)}}>
                    <Picker.Item label="Bovespa" value="^BVSP" />
                    <Picker.Item label="Dow Jones Industrial Average" value="^DJI" />
                    <Picker.Item label="NASDAQ Composto" value="^IXIC" />
                </Picker>
                <Picker  style={{ height: 50, width: 400,marginRight: 50 }} selectedValue = {acao} onValueChange ={(item,itemIndex) =>{setAcao(item)}}>
                    <Picker.Item label="Banco do Brasil" value="BBAS3.SA"/>
                    <Picker.Item label="Petrobras" value="PETR4.SA"  />	
                    <Picker.Item label="Vale" value="VALE5.SA"/>
                </Picker>
            </View>
            <View>
                <TouchableOpacity title = "Preditor1" onPress={total()} style = {css.login_button}><Text style = {css.login_buttonText}>Preditor2</Text> </TouchableOpacity>
            </View>
            </KeyboardAvoidingView>
        </SafeAreaView>
    );
    
}
